//** Update Meta data for one object_id **//
[templates.add update_object_meta]
//******return when object_id is empty******// [template.return template.object_id c.empty="template.object_id" /] [template.set app_user_email={app.session.email} default={app.user.email}/]
[template.set sql] set @object_id=[esc.id template.object_id /]; select ID from [esc.table template.meta_table /] where object_id=@object_id FOR UPDATE; [loop.@meta template.meta] set @cnt=0; set @coll_id=[esc.id template.coll_id /]; set @coll_type=[esc.id @meta.item.coll_type /]; set @meta_key=[esc.id @meta.item.meta_key /]; set @meta_value=[esc.str @meta.item.meta_value /]; set @email=[esc.str template.app_user_email /];
select @cnt:=count(1) from [esc.table template.meta_table /] where object_id=@object_id and coll_id=@coll_id and meta_key=@meta_key;
IF @cnt=0 THEN
insert into [esc.table template.meta_table /](object_id,coll_id,coll_type,meta_key,meta_value,updated_by) VALUES (@object_id,@coll_id,@coll_type,@meta_key,@meta_value,@email);
ELSE
update [esc.table template.meta_table /] set coll_type=@coll_type, meta_value=@meta_value, updated_by=@email, stamp=current_timestamp where ID in ( select ID from [esc.table template.meta_table /] where object_id=@object_id and coll_id=@coll_id and meta_key=@meta_key );
END IF ;
[/loop.@meta] [/template.set] [mysqli.transaction.commit o.set="template.transaction"] [template.sql /] [/mysqli.transaction.commit] [template.return template.transaction /] [/templates.add]
