//** Update One collection for One Object **// [templates.add update_coll_meta]
//******return when object_id is empty******// [template.return template.object_id c.empty="template.object_id" /]
[env.get app.user.email set=template.email/]
[template.set sql] [loop.@meta template.meta] SELECT @cnt:=count(1) FROM [esc.table template.meta_table /] WHERE object_id='[esc.unsafe template.object_id /]' AND coll_id='[esc.unsafe template.coll_id /]' AND coll_type='[esc.unsafe template.coll_type /]' AND meta_key='[esc.unsafe @meta.item.meta_key /]';
