SELECT '[esc.unsafe template.object_id /]','[esc.unsafe template.coll_id /]','[esc.unsafe template.coll_type /]','[esc.unsafe @meta.item.meta_key /]','[esc.unsafe @meta.item.meta_value /]','[esc.unsafe template.email /]' FROM dual WHERE @cnt=0;
UPDATE [esc.table template.meta_table /] SET meta_value='[esc.unsafe @meta.item.meta_value /]',updated_by='[esc.unsafe template.email /]',stamp=current_timestamp where object_id='[esc.unsafe template.object_id /]' and coll_id='[esc.unsafe template.coll_id /]' and coll_type='[esc.unsafe template.coll_type /]' and meta_key='[esc.unsafe @meta.item.meta_key /]' and @cnt=1; [/loop.@meta] [/template.set]
